{
  "description": "A secure, cross-platform password manager",
  "devDependencies": {
    "@types/node": "^20.10.0",
    "prettier": "^3.1.0",
    "turbo": "^2.0.0",
    "typescript": "^5.3.0"
  },
  "engines": {
    "node": ">=18.0.0"
  },
  "name": "birchvault",
  "packageManager": "pnpm@10.24.0",
  "private": true,
  "scripts": {
    "build": "turbo build",
    "clean": "turbo clean && rm -rf node_modules",
    "dev": "turbo dev",
    "format": "prettier --write \"**/*.{ts,tsx,js,jsx,json,md}\"",
    "lint": "turbo lint",
    "test": "turbo test"
  },
  "version": "0.1.0"
}

{
  "$schema": "https://schema.tauri.app/config/2.0.0",
  "app": {
    "security": {
      "csp": "default-src 'self'; script-src 'self'; style-src 'self' 'unsafe-inline'; img-src 'self' data: https:; connect-src 'self' https://*.supabase.co wss://*.supabase.co https://github.com https://*.github.com https://*.githubusercontent.com"
    },
    "windows": [
      {
        "center": true,
        "fullscreen": false,
        "height": 800,
        "minHeight": 600,
        "minWidth": 800,
        "resizable": true,
        "title": "BirchVault",
        "width": 1200
      }
    ]
  },
  "build": {
    "beforeBuildCommand": "pnpm build:frontend",
    "beforeDevCommand": "pnpm dev:frontend",
    "devUrl": "http://localhost:1420",
    "frontendDist": "../dist"
  },
  "bundle": {
    "active": true,
    "category": "Utility",
    "copyright": "Copyright © 2024 BirchVault",
    "createUpdaterArtifacts": true,
    "externalBin": [],
    "icon": [
      "icons/icon.ico"
    ],
    "linux": {
      "appimage": {
        "bundleMediaFramework": false
      }
    },
    "longDescription": "BirchVault is a secure, cross-platform password manager with end-to-end encryption.",
    "macOS": {
      "entitlements": null,
      "exceptionDomain": "",
      "frameworks": [],
      "minimumSystemVersion": "10.13"
    },
    "resources": [],
    "shortDescription": "Secure password manager",
    "targets": [
      "nsis"
    ],
    "windows": {
      "certificateThumbprint": null,
      "digestAlgorithm": "sha256",
      "timestampUrl": ""
    }
  },
  "identifier": "com.birchvault.app",
  "plugins": {
    "updater": {
      "endpoints": [
        "https://github.com/birchform/BirchVault/releases/latest/download/latest.json"
      ],
      "pubkey": "dW50cnVzdGVkIGNvbW1lbnQ6IG1pbmlzaWduIHB1YmxpYyBrZXk6IDY4RTVCQ0RGQjUwNkJGQzUKUldURnZ3YTEzN3psYU5TYW5tTG9NQlpCTm9mYnlpNHpzTXloQlE5RXF0dWJ4Q3lvNEJnbFlER1QK",
      "windows": {
        "installMode": "passive"
      }
    }
  },
  "productName": "BirchVault",
  "version": "0.1.0"
}

